    private void btnsimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnsimpanActionPerformed

    if  (txtnama.getText().isEmpty() || 
        txtttl.getText().isEmpty() || 
        (!rbcowo.isSelected() && !rbcewe.isSelected()) || 
        txtagama.getText().isEmpty() || 
        txtemail.getText().isEmpty() || 
        txttelp.getText().isEmpty() || 
        txtalamat.getText().isEmpty() || 
        txtbidang.getSelectedIndex() == -1) {

        javax.swing.JOptionPane.showMessageDialog(this, 
            "Harap lengkapi semua data.", 
            "Form Belum Lengkap", javax.swing.JOptionPane.ERROR_MESSAGE);
        
    } else if (!cekdata.isSelected()) {

        javax.swing.JOptionPane.showMessageDialog(this, 
            "Harap centang 'Data yang Saya Masukkan Sudah Benar' sebelum menyimpan.", 
            "Checkbox Belum Dicentang", javax.swing.JOptionPane.ERROR_MESSAGE);
        
    } else {

        String nama = txtnama.getText();
        String ttl = txtttl.getText();
        String gender = "";
            if (rbcowo.isSelected()) {
                gender = "Laki-laki";
            } else if (rbcewe.isSelected()) {
                gender = "Perempuan";
            }
        String agama = txtagama.getText();
        String email = txtemail.getText();
        String telp = txttelp.getText();
        String alamat = txtalamat.getText();
        String bidang = (String) txtbidang.getSelectedItem();
        
        Inidatanya data = new Inidatanya();
        data.setData(nama, ttl, gender, agama, email, telp, alamat, bidang);
        data.setVisible(true);
        this.dispose();
    }

    }//GEN-LAST:event_btnsimpanActionPerformed

