    private void btnsimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnsimpanActionPerformed
        // TODO add your handling code here:
        SMK2 frame1 = new SMK2();
        frame1.txtNL.setText(txtnl.getText());
        frame1.txtnp.setText(txtnp.getText());
        frame1.txtTL.setText(txttl.getText());
        if(rdbll.isSelected()){
            frame1.txtjl.setText("Laki - Laki");
        }
        if (rdbperempuan.isSelected()){
            frame1.txtjl.setText("Perempuan");
        }
        frame1.txtagama.setText(cmb.getSelectedItem().toString());
        frame1.txtas.setText(txtas.getText());
        frame1.txtalamat.setText(txtalamat.getText());
        frame1.txtnp.setText(txtnp.getText());
        frame1.txtnot.setText(txtnot.getText());
        frame1.txtpot.setText(txtpot.getText());
        frame1.txtaot.setText(txtaot.getText());
        frame1.txtpdd.setText(txtpendidikanot.getText());
        frame1.txtpeng.setText(txtpenghasilanot.getText());
        
        if (ckb.isSelected()){
           if(txtnl.getText().isEmpty()
            ||txtnp.getText().isEmpty()
            ||txttl.getText().isEmpty()
            ||txtas.getText().isEmpty()
            ||txtalamat.getText().isEmpty()
            ||txtnot.getText().isEmpty()
            ||txtpot.getText().isEmpty()
            ||txtaot.getText().isEmpty()
            ||txtpendidikanot.getText().isEmpty()
            ||txtpenghasilanot.getText().isEmpty()
            || buttonGroup1.equals(false)
                   );
        else
            frame1.setVisible(true);
             dispose();
               }
        
     
    }//GEN-LAST:event_btnsimpanActionPerformed

