    private void jBsearchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBsearchActionPerformed
        // TODO add your handling code here:
    String gejala = (String) kategori.getSelectedItem();  // Mendapatkan gejala yang dipilih
    String deskripsi = "";  // Inisialisasi deskripsi
    
    if (gejala.equals("Penghilang rasa sakit")) {
        deskripsi = "Obat yang dapat digunakan: \n" +
                    "Morfin, Petidin, Fentanil, Metadon: \n"
                +"Zat-zat ini digunakan untuk mengurangi \n"
                +"atau menghilangkan rasa sakit, terutama dalam konteks medis";
    } else if (gejala.equals("Obat subtitusi")) {
        deskripsi = "Obat yang dapat digunakan: \n" +
                    ".Metadon : digunakan untuk \n"
                + "pemulihan ketergantungan opioid \n"
                + "dan menekan gejala sakau.";
    } else if (gejala.equals("Anestesi")) {
        deskripsi = "Obat yang dapat digunakan: \n" +
                    " Fentanil, Petidin: \n "+ 
                "sebagai anastesi dan \n"+
                "untuk efek halusinogeniknya .";
    } else if (gejala.equals("Narkotika psikoaktif")) {
        deskripsi = "Obat yang dapat digunakan: \n" +
                    " Heroin, Ganja, MDMA,Trihexyphenidyl: \n"
                + "digunkan untuk efek rekreasi dan psikoaktif. .";
    } else if (gejala.equals("Stimulans")) {
        deskripsi = "Obat yang dapat digunakan: \n" +
                    " Ganja, MDMA,Trihexyphenidyl: \n"
                + " dapat merangsang sistem saraf pusat, \n"
                + "meningkatkan energi, kewaspadaan, dan aktivitas fisik..";
    }
    
    deskripsi1.setText(deskripsi);
    
    }//GEN-LAST:event_jBsearchActionPerformed

